        this.handlePrev = this.handlePrev.bind(this);
    }

    setBackground() {
        this.state.backgroundStyle.backgroundImage = "url(" + this.state.imgList[this.state.view] + ")"
    }

    handleNext() {
        if (this.state.view + 1 == this.state.imgList.length) {
            this.state.view = 0
        } else {
            this.setState({
                view: this.state.view + 1
            })
        }
    }

    handlePrev() {
        if (this.state.view == 0) {
            this.state.view = 0
        } else {
            this.setState({
                view: this.state.view - 1
            })
        }
    }

    render() {
        return (
            <div className="customerContainer">
                <div className="customerBackground" style={this.state.backgroundStyle} />
