class ProfileForm extends React.Component {
	constructor() {
		super();
		this.state = {
			formIsValid: false,
			formControls: {
				name: {
					value: '',
					placeholder: 'Customer name',
				},
				address: {
					value: '',
					placeholder: 'Customer address',
				},
				email: {
					value: '',
					placeholder: 'Customer email',
				},
				car: {
					value: '',
					placeholder: 'Customer Car'
				}
			}
		};

		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleChange(event) {

		const name = event.target.name;
		const value = event.target.value;

		this.setState({
			formControls: {
				...this.state.formControls,
				[name]: {
					...this.state.formControls[name],
					value
				}
			}
		});
	}

	handleSubmit(event) {
		event.preventDefault();
	}

	render() {
		return (
			<form onSubmit={this.handleSubmit}>
				<input type='text'
					name='name'
					placeholder={this.state.formControls.name.placeholder}
					value={this.state.formControls.name.value}
					onChange={this.handleChange}
				/>
				<br />
				<input type='text'
					name='address'
					value={this.state.formControls.address.value}
					placeholder={this.state.formControls.address.placeholder}
					onChange={this.handleChange}
				/>
				<br />
				<input type='email'
					name='email'
					value={this.state.formControls.email.value}
					placeholder={this.state.formControls.email.placeholder}
					onChange={this.handleChange}
				/>
				<br />
				<select value={this.state.formControls.car.value} onChange={this.handleChange}>
					<option value="Car A"> Car A </option>
					<option value="Car B"> Car B </option>
				</select>
				<br />
				<input type='submit' value='Update' />

			</form>
		);
	}
}

export default ProfileForm;
