import img0 from './bmw/bmw0.png'
import img1 from './bmw/bmw1.png'
import img2 from './bmw/bmw2.png'
import img3 from './bmw/bmw3.png'
import img4 from './bmw/bmw4.png'
import img5 from './bmw/bmw5.png'
import img6 from './bmw/bmw6.png'
import img7 from './bmw/bmw7.png'
import img8 from './bmw/bmw8.png'
import img9 from './bmw/bmw9.png'
import img10 from './bmw/bmw10.png'
import img11 from './bmw/bmw11.png'
import img12 from './bmw/bmw12.png'
import img13 from './bmw/bmw13.png'
import img14 from './bmw/bmw14.png'
import img15 from './bmw/bmw15.png'
import img16 from './bmw/bmw16.png'
import img17 from './bmw/bmw17.png'
import img18 from './bmw/bmw18.png'
import img19 from './bmw/bmw19.png'
import img20 from './bmw/bmw20.png'
import img21 from './bmw/bmw21.png'
import img22 from './bmw/bmw22.png'
import img23 from './bmw/bmw23.png'
import img24 from './bmw/bmw24.png'
import img25 from './bmw/bmw25.png'
import img26 from './bmw/bmw26.png'
import img27 from './bmw/bmw27.png'
import img28 from './bmw/bmw28.png'
import img29 from './bmw/bmw29.png'
import img30 from './bmw/bmw30.png'
import img31 from './bmw/bmw31.png'
import img32 from './bmw/bmw32.png'
import img33 from './bmw/bmw33.png'
import img34 from './bmw/bmw34.png'
import img35 from './bmw/bmw35.png'

class CustomerPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            view: 0,
            imgList: [img0, img1, img2, img3, img4, img5, img6, img7, img8, img9, img10, img11, img12, img13, img14, img15, img16, img17, img18, img19, img20, img21, img22, img23, img24, img25, img26, img27, img28, img29, img30, img31, img32, img33, img34, img35],
            backgroundStyle: {
                backgroundImage: "url(" + this.state.imgList[this.state.view] + ")"
            }
        }

        this.handleNext = this.handleNext.bind(this);
    }

    handleNext() {
        if (this.state.view + 1 == this.state.imgList.length) {
            this.state.view = 0
        } else {
            this.setState({
                view: this.state.view + 1
            })
        }
    }

    handlePrev() {
        if (this.state.view == 0) {
            this.state.view = 0
        } else {
            this.setState({
                view: this.state.view - 1
            })
        }
    }

    render() {
        return (
            <div className="customerContainer">
                <div className="customerBackground" style={this.state.backgroundStyle} />
                <div className="customerContent">
                    <button className="rotateButtonPrev" onClick={this.handlePrev} />
                    <button className="rotateButtonNext" onClick={this.handleNext} />
                </div>
            </div>
        );
    }
}

export default CustomerPage;
