import CustomerForm from '../components/CustomerForm'

class CustomerPage extends React.Component {

    constructor() {
        super()

        this.handleNext = this.handleNext.bind(this)
        this.handlePrev = this.handlePrev.bind(this)
        this.openCustomerMenu = this.openCustomerMenu.bind(this)
        this.openServiceMenu = this.openServiceMenu.bind(this)
        this.openFinanceMenu = this.openFinanceMenu.bind(this)
        this.closeMenu = this.closeMenu.bind(this)

        this.state = {
            view: 0,
            showCustomerView: false,
            showServiceView: false,
            showFinanceView: false
        }

        this.max = 35
    }

    handleNext() {
        if (this.state.view == this.max) {
            this.setState({
                view: 0
            })
        } else {
            this.setState({
                view: this.state.view + 1
            })
        }
    }

    handlePrev() {
        if (this.state.view == 0) {
            this.setState({
                view: this.max
            })
        } else {
            this.setState({
                view: this.state.view - 1
            })
        }
    }

    openCustomerMenu() {
        this.setState({
            showCustomerView: true
        })
    }

    openServiceMenu() {
        this.setState({
            showServiceView: true
        })
    }

    openFinanceMenu() {
        this.setState({
            showFinanceView: true
        })
    }

    closeCustomerMenu() {
        this.setState({
            showCustomerView: false,
            showServiceView: false,
            showFinanceView: false
        })
    }

    render() {

        return (
            <div className='customerContainer'>
                <div className='preload'>
                    {this.state.view == 0 ? <div className={'customerBackground' + this.max} /> : <div className={'customerBackground' + (this.state.view - 1)} />}
                    {this.state.view != this.max && <div className={'customerBackground' + (this.state.view + 1)} />}
                </div>
                <div className={'customerBackground' + this.state.view} />
                <div className='customerContent'>
                    <div className='buttonContainer'>
                        <button className="rotateButtonPrev" onClick={this.handlePrev} />
                        <button className="rotateButtonNext" onClick={this.handleNext} />
                    </div>
                    <div className='menuContainer'>
                        <button className='menuButton' />
                        <div className='dropdownContent'>
                            <button className='customerButton' onClick={this.openMenu} />
                            <button className='serviceButton' onClick={this.openMenu} />
                            <button className='financeButton' onClick={this.openMenu} />
                        </div>
                    </div>
                    {this.state.showCustomerView || this.state.showServiceView || this.state.showFinanceView ? <div className='menuViewContainer'>
                        <div className='menuViewOverlay' />
                        <div className='menuViewContent' >
                            <button className='closeButton' onClick={this.closeMenu} />
                            {this.state.showCustomerForm ? <div className='formContainer'>
                                <CustomerForm />
                            </div> : null}
                        </div>
                    </div > : null}
                </div>
            </div>
        );
    }
}

export default CustomerPage;
